"use strict";try{var utc=function utc(now){var datestring=now.toDateString()+" "+("0"+now.getHours()).slice(-2)+":"+("0"+now.getMinutes()).slice(-2)+":"+("0"+now.getSeconds()).slice(-2);return datestring};var ExportFromMatTable=new function(matTableSelector,filename){var table=document.querySelector(matTableSelector);if(!table){console.error('Table element "'+matTableSelector+'" not found.');return}if(!filename){filename="Export-From-MatTable"}var rows=Array.from(table.querySelectorAll("mat-row"));var headers=Array.from(table.querySelectorAll("mat-header-row mat-header-cell")).map(function(cell){return cell.innerText.replace(/,/g,"").trim()});var data=rows.map(function(row){return Array.from(row.querySelectorAll("mat-cell")).map(function(cell){return cell.innerText.replace(/,/g,"").trim()})});var filenameRow=Array(headers.length).fill("");var filenameRowIndex=Math.floor(filenameRow.length/2);filenameRow[filenameRowIndex]=filename;var csv="";csv+=filenameRow.join(",")+"\n";csv+=headers.join(",")+"\n";csv+=data.map(function(row){return row.join(",")}).join("\n");var csvBlob=new Blob([csv],{type:"text/csv;charset=utf-8;"});var csvURL=URL.createObjectURL(csvBlob);var downloadLink=document.createElement("a");downloadLink.href=csvURL;downloadLink.download=filename+" - "+utc(new Date)+".csv";downloadLink.style.display="none";document.body.appendChild(downloadLink);downloadLink.click();document.body.removeChild(downloadLink)}}catch(err){console.log("Please ignore it...",err)}module.exports={ExportFromMatTable:ExportFromMatTable};